package Assignment1;

public class Switch_Car {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String CarName = "Maruti";
		
		switch(CarName) {
		
		case "Nissan":
			
			System.out.println("This car will cost 20k");
			break;
			
		case "Toyota":
			
			System.out.println("This car is coming soon");
			break;
				
				
		case "Honda":
			
			System.out.println("This car is not avaiblable at the moment");
			break;
			
			
			default:
				
				System.out.println("Sorry we don't have this car");
				
	}
			
			
				

	}

}
